mapper.select:
SELECT notice_id, title, views, created_at, is_urgent
FROM (
     SELECT notice_id, title, views, created_at, is_urgent, ROWNUM as rnum
     FROM (
          SELECT notice_id, title, views, TO_CHAR(created_at, 'YYYY-MM-DD') as created_at, is_urgent
          FROM RECALL_NOTICE_DB
          ORDER BY is_urgent DESC, notice_id DESC
     )
     WHERE ROWNUM <= #{pageNum} * #{amount}
 )
WHERE rnum > (#{pageNum} - 1) * #{amount}

SELECT notice_id, title, content, views, TO_CHAR(created_at, 'YYYY-MM-DD HH24:MI') as created_at, is_urgent
FROM RECALL_NOTICE_DB
WHERE notice_id = #{notice_id}

mapper.update:
UPDATE RECALL_NOTICE_DB
SET views = views + 1
WHERE notice_id = #{notice_id}
