mapper.insert:
INSERT INTO RECALL (
MAKER, MODEL_NAME, MAKE_START, MAKE_END,
RECALL_DATE, RECALL_REASON
) VALUES (
#{maker}, #{modelName}, #{makeStart}, #{makeEnd},
#{recallDate}, #{recallReason}
)

mapper.select:
SELECT ID, MAKER, MODEL_NAME, MAKE_START, MAKE_END, RECALL_DATE, RECALL_REASON FROM RECALL
UPPER(MAKER) LIKE CONCAT('%', UPPER(#{keyword}), '%') OR UPPER(MODEL_NAME) LIKE CONCAT('%', UPPER(#{keyword}), '%')
ORDER BY ID DESC LIMIT #{amount} OFFSET ${(pageNum - 1) * amount}

SELECT ID, MAKER, MODEL_NAME, MAKE_START, MAKE_END, RECALL_DATE, RECALL_REASON
FROM RECALL
WHERE ID = #{id}
